package chapter2;

public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x,y;
    x = 2;
    y = 20;
     boolean result;
     
     result = x == y;
     System.out.println(result);
     
     
     result = x != y;
     System.out.println(result);
     
     
     result = x >y;
     System.out.println(result);
     
     
     result = x<y;
     System.out.println(result);
     
     
     result = x>=y;
     System.out.println(result);
     
     
     result = x<=y;
     System.out.println(result);
	}
	
	

}
