package Chapter4;

public class JavaWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// initialization int b = 10;
		//condition b>=0;
       //increment/decrement b -=2
		
		int b = 10;
		while (b>=0)  {
			System.out.println(b);
			b -= 2;
		}
		
		
	}

}
