package chapter3;

public class Relational_operation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 5;
		int y = 10;
		
		boolean result;
		
		result = x==y;
		System.out.println(result);
		
		result = x!=y;
		System.out.println(result);
		
		result = x<y;
		System.out.println(result);
		
		result = x>y;
		System.out.println(result);
		
		result = x>=y;
		System.out.println(result);
		
		result = x<=y;
		System.out.println(result);

	}

}
